    },
    linha:{
        width: '80%',
        height: 1,
        border: 1,
        backgroundColor: '#fff',
        marginTop: 30
    },
    areaRedesSociais:{
        margin: 10,
        flexDirection: 'row',
        height: 30,
        width: 120,
        justifyContent: 'center'
    },
    iconeSocial:{
        height: 30,
        width: 30,
        margin: 10
    },
    areaModoInicial:{
        flex
    }
})

export default style
